import React, {Component} from 'react';
import CardList from '../components/CardList';
import SearchBox from '../components/SearchBox';
import './App.css';
import Scroll from '../components/Scroll';

class App extends Component {
    constructor() {
        super()
        this.state = {
            robots: [],
            searchfield: ''
        }
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(users => {this.setState({robots: users})})
        this.setState({robots: this.removeTitle({robots})});
    }

    onSearchChange = event => { 
        this.setState({searchfield: event.target.value})
    }

    removeTitle = ({robots}) => {
        robots.map((user, i) => {
            if (robots[i].name.toLowerCase().startsWith('mr. ') || robots[i].name.toLowerCase().startsWith('ms. ')) {
                return robots[i].name.slice(4);
            } else if (robots[i].name.toLowerCase().startsWith('mrs. ')) {
                return robots[i].name.slice(5);
            } else {
                return robots[i].name;
            }
        })
    }

    render() {
        const {robots, searchfield} = this.state;
        const filteredRobots = robots.filter(robot => {
            return robot.name.toLowerCase().includes(searchfield.toLowerCase());
        })
        return !robots.length ?
            <h1 className='tc'>Loading...</h1> :
            <div classname='tc'>
                <h1 className='tc f1'>RoboFriends</h1>
                <SearchBox searchChange={this.onSearchChange} />
                <Scroll>
                    <CardList robots={filteredRobots}/>
                </Scroll>
            </div>;       
    }
}

export default App;
